//  String class test program
//
//  Tests: Character Array Contstructor
// 
  
#include "string.hpp"
#include <cassert>
#include <iostream>
 
//===========================================================================
int main ()
{
    {
        //------------------------------------------------------
        // SETUP FIXTURE

        // TEST
        String  str("Hello World");

        // VERIFY
        assert(str == "Hello World");
    }

    {
        //------------------------------------------------------
        // SETUP FIXTURE

        // TEST
        String  str("");

        // VERIFY
        assert(str == "");
    }

    {
        //------------------------------------------------------
        // SETUP FIXTURE

        // TEST
        String  str("Testing for a very big character array that is full of a lot of words");

        // VERIFY
        assert(str == "Testing for a very big character array that is full of a lot of words");
    }

    {
        //------------------------------------------------------
        // SETUP FIXTURE

        // TEST
        String  str("abcdefghijklmnopqrstuvwxyzabcdefghijklmnopqrstuvwxyzabcdefghijklmnopqrstuvwxyzabcdefghijklmnopqrstuvwxyzabcdefghijklmnopqrstuvwxyzabcdefghijklmnopqrstuvwxyzabcdefghijklmnopqrstuvwxyz");

        // VERIFY
        assert(str == "abcdefghijklmnopqrstuvwxyzabcdefghijklmnopqrstuvwxyzabcdefghijklmnopqrstuvwxyzabcdefghijklmnopqrstuvwxyzabcdefghijklmnopqrstuvwxyzabcdefghijklmnopqrstuvwxyzabcdefghijklmnopqrstuvwxyz");
    }

    {
        //------------------------------------------------------
        // SETUP FIXTURE

        // TEST
        String  str("");

        // VERIFY
        assert(str == "");
    }

    std::cout << "Done testing Character Array Contstructor." << std::endl;
    return 0;
}

